mapper.select:
SELECT BOARD_TYPE ,CODE_NAME ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT FROM ( SELECT B.BOARD_TYPE ,C.CODE_NAME ,B.BOARD_NUM ,B.BOARD_TITLE ,B.BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD B JOIN COM_CODE C ON B.BOARD_TYPE = C.CODE_ID WHERE 1 = 1
AND B.BOARD_TYPE IN
#{data}
) WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10

SELECT COUNT(*) AS TOTAL_CNT FROM BOARD WHERE 1 = 1
AND BOARD_TYPE IN
#{data}

mapper.insert:
        INSERT INTO BOARD (BOARD_TYPE
					        ,BOARD_NUM
					        ,BOARD_TITLE
					        ,BOARD_COMMENT
					        ,CREATOR
					        ,CREATE_TIME
					        ,MODIFIER
					        ,MODIFIED_TIME
        					)
        VALUES (#{boardType}
        		,(SELECT NVL(MAX(BOARD_NUM),0)+1  FROM BOARD)
		        ,#{boardTitle}
		        ,#{boardComment}
		        ,#{creator}
		        ,TO_CHAR(SYSDATE)
		        ,'SYSTEM'
		        ,TO_CHAR(SYSDATE)
        )

mapper.select:

    	SELECT CODE_NAME, CODE_ID
		FROM COM_CODE
		WHERE CODE_TYPE = #{codeType}

mapper.update:
    UPDATE BOARD SET
					BOARD_TITLE = #{boardTitle},
					BOARD_COMMENT = #{boardComment}
	WHERE BOARD_NUM = #{boardNum}

mapper.delete:
   		DELETE
		FROM  BOARD
		WHERE BOARD_TYPE = #{boardType}
		AND   BOARD_NUM  = #{boardNum}

mapper.select:
    SELECT COUNT(*)
	FROM USER_INFO
	WHERE USER_ID = #{userId}

SELECT USER_ID, USER_PW
FROM USER_INFO
WHERE USER_ID = #{userId}
